package ch04_01;

public class IfTest01 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int a  = 100;
		
		if(a>500) {
			System.out.println("조건식은 참입니다!!");
		} else {
			System.out.println("조건식은 거짓입니다!!");
		}
		
		System.out.println("저는 if문이 끝난뒤 나온 문장입니다.");
	}

}
